import Sidebar from "./Sidebar";
import { useState, useEffect } from "react";

export default function AppShell({ children, role }) {
  const [darkMode, setDarkMode] = useState(() =>
    localStorage.getItem("theme") === "dark"
  );

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  return (
    <div className="flex h-screen">
      <Sidebar role={role} darkMode={darkMode} onToggleTheme={() => setDarkMode(!darkMode)} />
      <main className="flex-1 p-4 overflow-y-auto bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-100">
        {children}
      </main>
    </div>
  );
}
