import { useState } from "react";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { db } from "../firebase";
import { useAuth } from "../context/AuthContext";

export default function UmpireReportForm() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    date: "",
    match: "",
    codeOfConduct: "",
    penalties: "",
    injuries: "",
    suspectActions: "",
  });
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const docRef = {
      ...formData,
      submittedBy: user.uid,
      submittedAt: serverTimestamp(),
    };
    await addDoc(collection(db, "umpireReports"), docRef);
    setSuccess(true);
    setFormData({
      date: "",
      match: "",
      codeOfConduct: "",
      penalties: "",
      injuries: "",
      suspectActions: "",
    });
  };

  return (
    <div className="max-w-2xl mx-auto p-4 bg-white dark:bg-gray-800 shadow rounded">
      <h2 className="text-2xl font-bold mb-6">Umpire Report</h2>
      {success && <p className="text-green-500 mb-6">Report submitted!</p>}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label htmlFor="date" className="block text-sm font-medium mb-1">
            Date
          </label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <div>
          <label htmlFor="match" className="block text-sm font-medium mb-1">
            Match
          </label>
          <input
            type="text"
            id="match"
            name="match"
            value={formData.match}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <div>
          <label htmlFor="codeOfConduct" className="block text-sm font-medium mb-1">
            Code of Conduct
          </label>
          <textarea
            id="codeOfConduct"
            name="codeOfConduct"
            value={formData.codeOfConduct}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <div>
          <label htmlFor="penalties" className="block text-sm font-medium mb-1">
            Penalties
          </label>
          <textarea
            id="penalties"
            name="penalties"
            value={formData.penalties}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <div>
          <label htmlFor="injuries" className="block text-sm font-medium mb-1">
            Injuries
          </label>
          <textarea
            id="injuries"
            name="injuries"
            value={formData.injuries}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <div>
          <label htmlFor="suspectActions" className="block text-sm font-medium mb-1">
            Suspect Actions
          </label>
          <textarea
            id="suspectActions"
            name="suspectActions"
            value={formData.suspectActions}
            onChange={handleChange}
            className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-md transition-colors duration-200"
        >
          Submit Umpire Report
        </button>
      </form>
    </div>
  );
}
