import { useState, useEffect } from "react";
import { db } from "../firebase";
import {
  collection,
  addDoc,
  getDocs,
  Timestamp,
  query,
  orderBy,
} from "firebase/firestore";

export default function TournamentAdmin() {
  const [tournaments, setTournaments] = useState([]);
  const [newTournament, setNewTournament] = useState("");

  useEffect(() => {
    const fetchTournaments = async () => {
      const q = query(collection(db, "tournaments"), orderBy("created", "desc"));
      const querySnapshot = await getDocs(q);
      const data = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setTournaments(data);
    };
    fetchTournaments();
  }, []);

  const handleAddTournament = async () => {
    if (!newTournament.trim()) return;
    const docRef = await addDoc(collection(db, "tournaments"), {
      name: newTournament.trim(),
      created: Timestamp.now(),
    });
    setTournaments([{ id: docRef.id, name: newTournament.trim(), created: new Date() }, ...tournaments]);
    setNewTournament("");
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Tournament Administration</h2>
      <div className="flex gap-2 mb-4">
        <input
          className="border p-2 rounded w-full"
          value={newTournament}
          onChange={(e) => setNewTournament(e.target.value)}
          placeholder="Enter new tournament name"
        />
        <button onClick={handleAddTournament} className="bg-blue-600 text-white px-4 py-2 rounded">
          Add
        </button>
      </div>
      <ul className="space-y-2">
        {tournaments.map((t) => (
          <li key={t.id} className="border p-2 rounded bg-white dark:bg-gray-700 dark:text-white">
            {t.name} <span className="text-sm text-gray-500 ml-2">({new Date(t.created.seconds * 1000).toLocaleDateString()})</span>
          </li>
        ))}
      </ul>
    </div>
  );
}
