import { useState, useEffect } from "react";
import { useTournament } from "../context/TournamentContext";
import Papa from "papaparse";
import { collection, doc, Timestamp, writeBatch } from "firebase/firestore";
import { db } from "../firebase";
import dayjs from "dayjs";

export default function UploadDraw() {
  const { tournaments, currentTournament, setCurrentTournament } = useTournament();
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [showTournamentSelector, setShowTournamentSelector] = useState(false);

  // Auto-select logic
  useEffect(() => {
    if (tournaments.length > 0 && !currentTournament) {
      const activeTournament = tournaments.find(t => t.status === 'active');
      if (activeTournament) {
        setCurrentTournament(activeTournament);
      } else {
        setCurrentTournament(tournaments[0]);
      }
    }
  }, [tournaments, currentTournament, setCurrentTournament]);

  const handleUpload = async () => {
    if (!currentTournament?.id) {
      setStatus("❌ Please select a tournament first");
      return;
    }

    if (!file) {
      setStatus("❌ Please select a CSV file first");
      return;
    }

    setIsUploading(true);
    setStatus("Processing matches...");

    try {
      const results = await new Promise((resolve, reject) => {
        Papa.parse(file, {
          header: true,
          complete: resolve,
          error: reject
        });
      });

      const batch = writeBatch(db);
      const matchesCollection = collection(db, `tournaments/${currentTournament.id}/matches`);
      let successCount = 0;
      let errorCount = 0;

      for (const row of results.data) {
        try {
          const normalized = normalizeRow(row);
          validateRow(normalized);

          const match = {
            date: Timestamp.fromDate(dayjs(normalized.date, ['DD/MM/YYYY', 'YYYY-MM-DD']).toDate()),
            time: normalized.time,
            division: normalized.division,
            home: normalized.home,
            away: normalized.away,
            umpire: normalized.umpire || '',
            court: normalized.court || '1',
            tournamentId: currentTournament.id,
            createdAt: Timestamp.now()
          };

          const docRef = doc(matchesCollection);
          batch.set(docRef, match);
          successCount++;
        } catch (error) {
          console.warn("Skipping invalid row:", error.message);
          errorCount++;
        }
      }

      await batch.commit();
      setStatus(
        `✅ Successfully uploaded ${successCount} matches` +
        (errorCount > 0 ? ` (${errorCount} invalid rows skipped)` : "")
      );
    } catch (error) {
      console.error("Upload failed:", error);
      setStatus(`❌ Upload failed: ${error.message}`);
    } finally {
      setIsUploading(false);
    }
  };

  // Helper functions
  const normalizeRow = (row) => {
    return Object.fromEntries(
      Object.entries(row).map(([k, v]) => 
        [k.toLowerCase().trim(), typeof v === 'string' ? v.trim() : v]
      )
    );
  };

  const validateRow = (row) => {
    const requiredFields = ['date', 'time', 'division', 'home', 'away'];
    const missing = requiredFields.filter(f => !row[f]);
    if (missing.length > 0) {
      throw new Error(`Missing required fields: ${missing.join(', ')}`);
    }

    const date = dayjs(row.date, ['DD/MM/YYYY', 'YYYY-MM-DD'], true);
    if (!date.isValid()) {
      throw new Error(`Invalid date format: ${row.date}`);
    }

    const timeRegex = /^([0-1]?[0-9]|2[0-3]):[0-5][0-9]$/;
    if (!timeRegex.test(row.time)) {
      throw new Error(`Invalid time format: ${row.time}`);
    }
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white">
          {currentTournament?.name || "Match Schedule Upload"}
        </h2>
        {currentTournament && (
          <button
            onClick={() => setShowTournamentSelector(true)}
            className="text-blue-400 hover:text-blue-300 text-sm"
          >
            Change Tournament
          </button>
        )}
      </div>

      {/* Tournament Selection */}
      {(!currentTournament || showTournamentSelector) && (
        <div className="bg-gray-800 p-6 rounded-lg mb-6">
          {tournaments.length === 0 ? (
            <div className="text-red-400 p-4 bg-red-900 rounded">
              No tournaments available. Please create a tournament first.
            </div>
          ) : (
            <>
              <label className="block text-white mb-2">
                Select Tournament:
              </label>
              <select
                className="w-full p-3 rounded bg-white text-black"
                onChange={(e) => {
                  const tournament = tournaments.find(t => t.id === e.target.value);
                  setCurrentTournament(tournament);
                  setShowTournamentSelector(false);
                }}
                value={currentTournament?.id || ''}
              >
                <option value="">Select a tournament</option>
                {tournaments.map(t => (
                  <option key={t.id} value={t.id}>
                    {t.name} {t.status === 'active' && '★'}
                  </option>
                ))}
              </select>
            </>
          )}
        </div>
      )}

      {/* Main Upload Interface */}
      {currentTournament && !showTournamentSelector && (
        <>
          <div className="bg-gray-800 p-6 rounded-lg">
            <div className="mb-4">
              <label className="block text-white mb-2">
                Select Match Schedule CSV:
              </label>
              <input
                type="file"
                accept=".csv"
                onChange={(e) => setFile(e.target.files[0])}
                className="block w-full text-white file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:bg-blue-600 file:text-white"
              />
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || isUploading}
              className={`px-4 py-2 rounded text-white ${
                !file || isUploading
                  ? 'bg-gray-500 cursor-not-allowed' 
                  : 'bg-green-600 hover:bg-green-700'
              }`}
            >
              {isUploading ? 'Uploading...' : 'Upload Matches'}
            </button>

            {status && (
              <div className={`mt-4 p-3 rounded ${
                status.startsWith('✅') 
                  ? 'bg-green-900 text-green-100' 
                  : 'bg-red-900 text-red-100'
              }`}>
                {status}
              </div>
            )}
          </div>

          <div className="mt-8 bg-gray-800 p-4 rounded-lg">
            <h3 className="text-white font-medium mb-2">CSV Format Requirements</h3>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-gray-900 text-gray-300 text-sm">
                <thead>
                  <tr>
                    <th className="px-4 py-2 border border-gray-700">Date</th>
                    <th className="px-4 py-2 border border-gray-700">Time</th>
                    <th className="px-4 py-2 border border-gray-700">Court</th>
                    <th className="px-4 py-2 border border-gray-700">Division</th>
                    <th className="px-4 py-2 border border-gray-700">Home</th>
                    <th className="px-4 py-2 border border-gray-700">Away</th>
                    <th className="px-4 py-2 border border-gray-700">Umpire</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td className="px-4 py-2 border border-gray-700">06/05/2025</td>
                    <td className="px-4 py-2 border border-gray-700">19:30</td>
                    <td className="px-4 py-2 border border-gray-700">Court 1</td>
                    <td className="px-4 py-2 border border-gray-700">WOMEN</td>
                    <td className="px-4 py-2 border border-gray-700">ACT</td>
                    <td className="px-4 py-2 border border-gray-700">QLD</td>
                    <td className="px-4 py-2 border border-gray-700">James Patel</td>
                  </tr>
                </tbody>
              </table>
            </div>
            <div className="text-gray-400 text-sm mt-2 space-y-1">
              <p>• Date formats: DD/MM/YYYY or YYYY-MM-DD</p>
              <p>• Time format: 24-hour (e.g., 19:30)</p>
              <p>• Court defaults to "1" if empty</p>
              <p>• Umpire field is optional</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
