import { useState, useEffect } from "react";
import { collection, getDocs, doc, updateDoc, query, where } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";
import dayjs from "dayjs";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [timeoutReached, setTimeoutReached] = useState(false);

  // Set a timeout as fallback
  useEffect(() => {
    const timer = setTimeout(() => {
      if (loading) {
        setTimeoutReached(true);
        setError("Loading is taking longer than expected. Please check your connection.");
        setLoading(false);
      }
    }, 10000); // 10 second timeout

    return () => clearTimeout(timer);
  }, [loading]);

  useEffect(() => {
    let isMounted = true; // Track mounted state

    const fetchData = async () => {
      try {
        if (!currentTournament?.id) {
          setLoading(false);
          return;
        }

        setLoading(true);
        setError(null);

        // Fetch matches with error handling for each operation
        const matchesQuery = query(
          collection(db, `tournaments/${currentTournament.id}/matches`),
          where("date", ">=", new Date().toISOString().split('T')[0])
        );

        const [matchesSnapshot, usersSnapshot] = await Promise.all([
          getDocs(matchesQuery).catch(err => {
            console.error("Matches fetch error:", err);
            throw new Error("Failed to load matches");
          }),

          getDocs(query(
            collection(db, "users"),
            where("role", "==", "umpire")
          )).catch(err => {
            console.error("Umpires fetch error:", err);
            throw new Error("Failed to load umpires");
          })
        ]);

        if (!isMounted) return;

        const matchesData = matchesSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
          date: doc.data().date?.toDate?.() || doc.data().date,
          time: doc.data().time || "00:00"
        }));

        const umpiresData = usersSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
          fullName: `${doc.data().firstName || ''} ${doc.data().lastName || ''}`.trim()
        }));

        setMatches(matchesData);
        setUmpires(umpiresData);

      } catch (err) {
        console.error("Data fetch error:", err);
        if (isMounted) {
          setError(err.message || "Failed to load data");
        }
      } finally {
        if (isMounted) {
          setLoading(false);
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false; // Cleanup function
    };
  }, [currentTournament?.id]);

  const handleAssignUmpire = async (matchId, umpireId) => {
    try {
      const umpire = umpires.find(u => u.id === umpireId);
      if (!umpire || !currentTournament?.id) return;

      await updateDoc(
        doc(db, `tournaments/${currentTournament.id}/matches`, matchId),
        { umpire: umpire.fullName, umpireId: umpire.id }
      );

      setMatches(prev => 
        prev.map(m => 
          m.id === matchId 
            ? { ...m, umpire: umpire.fullName, umpireId: umpire.id } 
            : m
        )
      );
    } catch (err) {
      console.error("Assignment error:", err);
      setError("Failed to assign umpire");
    }
  };

  // Render loading state with timeout message
  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center h-64">
        <div className="text-white mb-2">Loading matches and umpires...</div>
        {timeoutReached && (
          <div className="text-yellow-300 text-sm">
            Taking longer than expected. Still trying...
          </div>
        )}
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="p-4 bg-red-900 rounded text-white">
        <p className="font-bold">Error:</p>
        <p>{error}</p>
        <button 
          onClick={() => window.location.reload()}
          className="mt-2 px-4 py-2 bg-white text-red-900 rounded"
        >
          Retry
        </button>
      </div>
    );
  }

  // No tournament selected
  if (!currentTournament) {
    return (
      <div className="p-4 bg-blue-900 rounded text-white">
        Please select a tournament from the dropdown
      </div>
    );
  }

  // No matches found
  if (matches.length === 0) {
    return (
      <div className="p-4 bg-gray-800 rounded text-white">
        No upcoming matches found for {currentTournament.name}
      </div>
    );
  }

  // Group matches by date
  const matchesByDate = matches.reduce((acc, match) => {
    const dateKey = dayjs(match.date).format("YYYY-MM-DD");
    if (!acc[dateKey]) acc[dateKey] = [];
    acc[dateKey].push(match);
    return acc;
  }, {});

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold text-white mb-6">
        Umpire Appointments for {currentTournament.name}
      </h2>

      {Object.entries(matchesByDate)
        .sort(([dateA], [dateB]) => new Date(dateA) - new Date(dateB))
        .map(([date, dateMatches]) => (
          <div key={date} className="mb-8 bg-gray-800 bg-opacity-50 rounded-lg p-4">
            <h3 className="text-xl font-semibold text-white mb-4">
              {dayjs(date).format("dddd, D MMMM YYYY")}
            </h3>

            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-700">
                  <tr>
                    <th className="p-3 text-left text-white">Time</th>
                    <th className="p-3 text-left text-white">Division</th>
                    <th className="p-3 text-left text-white">Teams</th>
                    <th className="p-3 text-left text-white">Umpire Assignment</th>
                  </tr>
                </thead>
                <tbody>
                  {dateMatches
                    .sort((a, b) => a.time.localeCompare(b.time))
                    .map((match) => (
                      <tr key={match.id} className="border-t border-gray-700">
                        <td className="p-3 text-white">{match.time}</td>
                        <td className="p-3 text-white">{match.division}</td>
                        <td className="p-3 text-white">{match.teams || "TBD vs TBD"}</td>
                        <td className="p-3">
                          <select
                            className="w-full p-2 rounded bg-white text-black"
                            value={match.umpireId || ""}
                            onChange={(e) => handleAssignUmpire(match.id, e.target.value)}
                            disabled={loading}
                          >
                            <option value="">Select Umpire</option>
                            {umpires.map((umpire) => (
                              <option key={umpire.id} value={umpire.id}>
                                {umpire.fullName}
                              </option>
                            ))}
                          </select>
                          {match.umpire && (
                            <div className="text-sm text-gray-300 mt-1">
                              Currently: {match.umpire}
                            </div>
                          )}
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          </div>
        ))}
    </div>
  );
}
