import { useState } from "react";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { db } from "../firebase";
import { useAuth } from "../context/AuthContext";

export default function UmpireReportForm() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    date: "",
    match: "",
    codeOfConduct: "",
    penalties: "",
    injuries: "",
    suspectActions: "",
  });
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const docRef = {
      ...formData,
      submittedBy: user.uid,
      submittedAt: serverTimestamp(),
    };
    await addDoc(collection(db, "umpireReports"), docRef);
    setSuccess(true);
    setFormData({
      date: "",
      match: "",
      codeOfConduct: "",
      penalties: "",
      injuries: "",
      suspectActions: "",
    });
  };

  return (
    <div className="max-w-2xl mx-auto p-4 bg-white dark:bg-gray-800 shadow rounded">
      <h2 className="text-xl font-bold mb-4">Umpire Report</h2>
      {success && <p className="text-green-500 mb-4">Report submitted!</p>}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
        {["date", "match", "codeOfConduct", "penalties", "injuries", "suspectActions"].map((field) => (
          <textarea
            key={field}
            name={field}
            value={formData[field]}
            onChange={handleChange}
            placeholder={field.replace(/([A-Z])/g, " $1")}
            className="p-2 border rounded"
          />
        ))}
        <button type="submit" className="bg-blue-600 text-white py-2 rounded">
          Submit Umpire Report
        </button>
      </form>
    </div>
  );
}
