import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import Unauthorized from "./pages/Unauthorized";
import MatchReportForm from "./pages/MatchReportForm";
import UmpireReportForm from "./pages/UmpireReportForm";
import AdminUsers from "./pages/AdminUsers";
import AppShell from "./components/AppShell";
import { AuthProvider, useAuth } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import SignUp from "./pages/SignUp";
import Agenda from "./pages/Agenda";
import UmpireAppointments from "./pages/UmpireAppointments";
import MyDetails from "./pages/MyDetails";
import TournamentSettings from "./pages/TournamentSettings";
import { TournamentProvider } from "./context/TournamentContext";
import UploadDraw from "./pages/UploadDraw";

function ShellWrapper({ children }) {
  const { role } = useAuth();
  return (
    <AppShell role={role}>
      <div className="text-gray-900 dark:text-gray-100 bg-white dark:bg-gray-900 min-h-screen">
        <div>
          {children}
        </div>
      </div>
    </AppShell>
  );
}

function App() {
  return (
    <AuthProvider>
      <TournamentProvider>
        <Router>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/unauthorized" element={<Unauthorized />} />

            <Route
              path="/dashboard"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <Dashboard />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />
            <Route
              path="/match-report"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <MatchReportForm />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/umpire-report"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <UmpireReportForm />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/admin/users"
              element={
                <ProtectedRoute requireAdmin={true}>
                  <ShellWrapper>
                    <AdminUsers />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/admin/appointments"
              element={
                <ProtectedRoute requireAdmin={true}>
                  <ShellWrapper>
                    <UmpireAppointments />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/agenda"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <Agenda />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/settings/TournamentAdmin"
              element={
                <ProtectedRoute requireAdmin={true}>
                  <ShellWrapper>
                    <TournamentSettings />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/settings/details"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <MyDetails />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route
              path="/admin/upload"
              element={
                <ProtectedRoute requireAdmin={true}>
                  <ShellWrapper>
                    <UploadDraw />
                  </ShellWrapper>
                </ProtectedRoute>
              }
            />

            <Route path="*" element={<Login />} />
          </Routes>
        </Router>
      </TournamentProvider>
    </AuthProvider>
  );
}

export default App;
