import { useState } from "react";
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from "../firebase";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate("/dashboard");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto bg-white dark:bg-gray-900 rounded shadow-md">
      <h1 className="text-xl font-bold mb-4 text-black dark:text-white">Login</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input className="w-full p-2 border rounded bg-white text-black dark:bg-gray-700 dark:text-white dark:placeholder-gray-400" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input className="w-full p-2 border rounded bg-white text-black dark:bg-gray-700 dark:text-white dark:placeholder-gray-400" placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded">Login</button>
        {error && <p className="text-red-500">{error}</p>}
        <p className="text-sm text-blue-500 mt-4 text-center">
          New here? <Link to="/signup" className="underline">Create an account</Link>
        </p>
      </form>
    </div>
  );
}
