import { useState } from "react";
import { collection, addDoc, setDoc, doc } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";

export default function UploadDraw() {
  const { currentTournament } = useTournament();
  const [matchData, setMatchData] = useState({});

  const handleUpload = async () => {
    if (!currentTournament) {
      alert("Please select a tournament first.");
      return;
    }
    try {
      const matchesCollection = collection(db, `tournaments/${currentTournament}/matches`);
      const matchId = matchData.id || ""; // assuming matchData has an id field
      if (matchId) {
        await setDoc(doc(matchesCollection, matchId), { ...matchData });
      } else {
        await addDoc(matchesCollection, { ...matchData });
      }
      alert("Match uploaded successfully.");
    } catch (error) {
      console.error("Error uploading match: ", error);
      alert("Failed to upload match.");
    }
  };

  // Assuming matchesByTime is an object like { "18:00": [match1, match2], "19:30": [match3] }
  // This part is added to demonstrate the rendering changes requested.

  const matchesByTime = {}; // placeholder for actual data

  return (
    <div>
      {/* UI for uploading draw and setting matchData */}
      <button onClick={handleUpload}>Upload Draw</button>

      {Object.entries(matchesByTime).map(([timeSlot, matchesAtTime]) => (
        <div key={timeSlot}>
          <h3 className="text-lg font-bold text-white mt-6 mb-2">{timeSlot}</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {matchesAtTime.map(match => (
              <div key={match.id} className="bg-blue-600 p-4 rounded shadow-md">
                <div className="text-white text-sm">
                  <p className="font-semibold">Court: {match.court || "N/A"}</p>
                  <p>Division: {match.division || "N/A"}</p>
                  <p>Teams: {match.teams || "TBD vs TBD"}</p>
                  <p className="italic">Umpire: {match.umpire || "Unassigned"}</p>
                  <p className="text-xs mt-2">Match ID: {match.id}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
