import { useState } from "react";
import { useTournament } from "../context/TournamentContext";
import Papa from "papaparse";
import { collection, doc, Timestamp, writeBatch } from "firebase/firestore";
import { db } from "../firebase";
import dayjs from "dayjs";

export default function UploadDraw() {
  const { currentTournament, tournaments, setCurrentTournament } = useTournament();
  const [localTournament, setLocalTournament] = useState(null);
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");
  const [isUploading, setIsUploading] = useState(false);

  const handleUpload = async () => {
    // 1. Verify active tournament exists
    if (!currentTournament?.id) {
      setStatus("❌ No active tournament selected in the system");
      return;
    }

    // 2. Verify file is selected
    if (!file) {
      setStatus("❌ Please select a CSV file first");
      return;
    }

    setIsUploading(true);
    setStatus("Processing matches for current tournament...");

    Papa.parse(file, {
      header: true,
      complete: async (results) => {
        try {
          const batch = writeBatch(db);
          const matchesCollection = collection(db, `tournaments/${currentTournament.id}/matches`);
          let successCount = 0;
          let skippedCount = 0;

          for (const row of results.data) {
            // Normalize column names to lowercase
            const normalizedRow = Object.fromEntries(
              Object.entries(row).map(([k, v]) => [k.toLowerCase(), v])
            );

            // Validate required fields
            if (!normalizedRow.date || !normalizedRow.time || !normalizedRow.division || 
                !normalizedRow.home || !normalizedRow.away) {
              skippedCount++;
              continue;
            }

            // Parse date (handle multiple formats)
            const parsedDate = dayjs(normalizedRow.date, ['DD/MM/YYYY', 'D/M/YYYY', 'YYYY-MM-DD']).toDate();
            if (isNaN(parsedDate.getTime())) {
              skippedCount++;
              continue;
            }

            // Create match object
            const match = {
              date: Timestamp.fromDate(parsedDate),
              time: normalizedRow.time.trim(),
              division: normalizedRow.division.trim(),
              home: normalizedRow.home.trim(),
              away: normalizedRow.away.trim(),
              umpire: normalizedRow.umpire?.trim() || "",
              court: normalizedRow.court?.trim() || "1", // Default court
              tournamentId: currentTournament.id,
              createdAt: Timestamp.now()
            };

            // Add to batch
            const docRef = doc(matchesCollection);
            batch.set(docRef, match);
            successCount++;
          }

          await batch.commit();
          setStatus(
            `✅ Uploaded ${successCount} matches to ${currentTournament.name}` +
            (skippedCount > 0 ? ` (${skippedCount} rows skipped due to missing/invalid data)` : "")
          );
        } catch (error) {
          console.error("Upload error:", error);
          setStatus(`❌ Failed to upload: ${error.message}`);
        } finally {
          setIsUploading(false);
        }
      },
      error: (error) => {
        setStatus("❌ Invalid CSV format");
        setIsUploading(false);
      }
    });
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-white">
        Upload to: {currentTournament?.name || "No Active Tournament"}
      </h2>

      {!currentTournament ? (
        <div className="bg-red-900 text-white p-4 rounded">
          <p>Please set an active tournament first before uploading matches</p>
          <p className="text-sm mt-2">Use the tournament selector in the sidebar</p>
        </div>
      ) : (
        <>
          <div className="bg-gray-800 p-6 rounded-lg">
            <div className="mb-4">
              <label className="block text-white mb-2">
                Select Match Schedule CSV:
              </label>
              <input
                type="file"
                accept=".csv"
                onChange={(e) => setFile(e.target.files[0])}
                className="block w-full text-white file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:bg-blue-600 file:text-white"
              />
            </div>

            <button
              onClick={handleUpload}
              disabled={!file || isUploading || !currentTournament}
              className={`px-4 py-2 rounded text-white ${
                !file || isUploading || !currentTournament
                  ? 'bg-gray-500 cursor-not-allowed' 
                  : 'bg-green-600 hover:bg-green-700'
              }`}
            >
              {isUploading ? 'Uploading...' : 'Upload to Current Tournament'}
            </button>

            {status && (
              <div className={`mt-4 p-3 rounded ${
                status.startsWith('✅') 
                  ? 'bg-green-900 text-green-100' 
                  : 'bg-red-900 text-red-100'
              }`}>
                {status}
              </div>
            )}
          </div>
          <div className="mt-8 bg-gray-800 p-4 rounded-lg">
  <h3 className="text-white font-medium mb-2">Required CSV Format:</h3>
  <div className="overflow-x-auto">
    <table className="min-w-full bg-gray-900 text-gray-300 text-sm">
      <thead>
        <tr>
          <th className="px-4 py-2 border border-gray-700">Date</th>
          <th className="px-4 py-2 border border-gray-700">Time</th>
          <th className="px-4 py-2 border border-gray-700">Court</th>
          <th className="px-4 py-2 border border-gray-700">Division</th>
          <th className="px-4 py-2 border border-gray-700">Home</th>
          <th className="px-4 py-2 border border-gray-700">Away</th>
          <th className="px-4 py-2 border border-gray-700">Umpire</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td className="px-4 py-2 border border-gray-700">06/05/2025</td>
          <td className="px-4 py-2 border border-gray-700">19:30</td>
          <td className="px-4 py-2 border border-gray-700">Court 1</td>
          <td className="px-4 py-2 border border-gray-700">WOMEN</td>
          <td className="px-4 py-2 border border-gray-700">ACT</td>
          <td className="px-4 py-2 border border-gray-700">QLD</td>
          <td className="px-4 py-2 border border-gray-700">James Patel</td>
        </tr>
      </tbody>
    </table>
  </div>
  <div className="text-gray-400 text-sm mt-2 space-y-1">
    <p>Note: All matches will be uploaded to {currentTournament?.name || 'the current tournament'}</p>
    <p>• Date format: DD/MM/YYYY or YYYY-MM-DD</p>
    <p>• Time format: HH:mm (24-hour time)</p>
    <p>• Division: Case-sensitive (exactly as you want it displayed)</p>
    <p>• Court will default to "1" if not specified</p>
    </div>
          </div>
        </>
      )}
    </div>
  );
}
