// src/context/TournamentContext.js
import { createContext, useContext, useState, useEffect } from "react";
import { db } from "../firebase";
import { collection, onSnapshot } from "firebase/firestore";

// Create and export the context object
export const TournamentContext = createContext();

export function TournamentProvider({ children }) {
  const [currentTournament, setCurrentTournament] = useState(null);
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "tournaments"), (snapshot) => {
      const data = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setTournaments(data);
    });
    return unsubscribe;
  }, []);

  return (
    <TournamentContext.Provider value={{ currentTournament, setCurrentTournament, tournaments }}>
      {children}
    </TournamentContext.Provider>
  );
}
// When setting current tournament
setCurrentTournament({
  id: tournamentDoc.id,
  name: tournamentDoc.data().name,
  // other relevant fields
});
// Export the custom hook
export function useTournament() {
  const context = useContext(TournamentContext);
  if (context === undefined) {
    throw new Error('useTournament must be used within a TournamentProvider');
  }
  return context;
}
