import { useState } from "react";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { db } from "../firebase";
import { useAuth } from "../context/AuthContext";

export default function UmpireReportForm() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    date: "",
    match: "",
    codeOfConduct: "",
    penalties: "",
    injuries: "",
    suspectActions: "",
  });
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const docRef = {
      ...formData,
      submittedBy: user.uid,
      submittedAt: serverTimestamp(),
    };
    await addDoc(collection(db, "umpireReports"), docRef);
    setSuccess(true);
    setFormData({
      date: "",
      match: "",
      codeOfConduct: "",
      penalties: "",
      injuries: "",
      suspectActions: "",
    });
  };

  return (
    <div className="max-w-2xl mx-auto p-4 bg-white dark:bg-gray-800 shadow rounded">
      <h2 className="text-xl font-bold mb-4">Umpire Report</h2>
      {success && <p className="text-green-500 mb-4">Report submitted!</p>}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4">
        <label>
          Date
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <label>
          Match
          <input
            type="text"
            name="match"
            value={formData.match}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <label>
          Code of Conduct
          <textarea
            name="codeOfConduct"
            value={formData.codeOfConduct}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <label>
          Penalties
          <textarea
            name="penalties"
            value={formData.penalties}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <label>
          Injuries
          <textarea
            name="injuries"
            value={formData.injuries}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <label>
          Suspect Actions
          <textarea
            name="suspectActions"
            value={formData.suspectActions}
            onChange={handleChange}
            className="p-2 border rounded dark:bg-gray-700 dark:text-white dark:border-gray-600 w-full"
          />
        </label>
        <button type="submit" className="bg-blue-600 text-white py-2 rounded">
          Submit Umpire Report
        </button>
      </form>
    </div>
  );
}
