import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Dashboard from "./pages/Dashboard";
import Login from "./pages/Login";
import Unauthorized from "./pages/Unauthorized";
import MatchReportForm from "./pages/MatchReportForm";
import UmpireReportForm from "./pages/UmpireReportForm";
import AdminUsers from "./pages/AdminUsers";
import AppShell from "./components/AppShell";
import { AuthProvider, useAuth } from "./context/AuthContext";
import ProtectedRoute from "./components/ProtectedRoute";
import SignUp from "./pages/SignUp";
import Agenda from "./pages/Agenda";
import UmpireAppointments from "./pages/UmpireAppointments";
import TournamentAdmin from "./pages/TournamentAdmin";
import MyDetails from "./pages/MyDetails";

function ShellWrapper({ children }) {
  const { role } = useAuth();
  return <AppShell role={role}>{children}</AppShell>;
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/unauthorized" element={<Unauthorized />} />

          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <ShellWrapper>
                  <Dashboard />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/match-report"
            element={
              <ProtectedRoute>
                <ShellWrapper>
                  <MatchReportForm />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/umpire-report"
            element={
              <ProtectedRoute>
                <ShellWrapper>
                  <UmpireReportForm />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/admin/users"
            element={
              <ProtectedRoute requireAdmin={true}>
                <ShellWrapper>
                  <AdminUsers />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/admin/UmpireAppointments"
            element={
              <ProtectedRoute>
                <ShellWrapper>
                  <UmpireAppointments />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/agenda"
            element={
              <ProtectedRoute>
                <ShellWrapper>
                  <Agenda />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />

          <Route
            path="/settings/tournamentadmin"
            element={
              <ProtectedRoute requireAdmin={true}>
                <ShellWrapper>
                  <TournamentAdmin />
                </ShellWrapper>
              </ProtectedRoute>
            }
          />
            <Route
              path="/settings/details"
              element={
                <ProtectedRoute>
                  <ShellWrapper>
                    <MyDetails />
                  </ShellWrapper>
                </ProtectedRoute>
            }
          />
            <Route 
              path="/settings/tournamentadmin" 
              element={
                <ProtectedRoute requireAdmin={true}>
                  <ShellWrapper>
                    <TournamentSettings />
                  </ShellWrapper>
                </ProtectedRoute>
            } 
          />
          <Route path="*" element={<Login />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
