import { useEffect, useState } from "react";
import { db } from "../firebase";
import { collection, query, where, getDocs, orderBy } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";
import dayjs from "dayjs";

export default function Agenda() {
  const { user } = useAuth();
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchMatches = async () => {
      if (!user) return;

      const userName = user?.displayName || ""; // replace if storing team info elsewhere
      const matchRef = collection(db, "matches");

      const q = query(
        matchRef,
        where("date", ">=", dayjs().format("YYYY-MM-DD")),
        orderBy("date"),
        orderBy("time")
      );

      const snapshot = await getDocs(q);
      const userMatches = snapshot.docs
        .map(doc => ({ id: doc.id, ...doc.data() }))
        .filter(
          match =>
            match.team1 === userName ||
            match.team2 === userName ||
            match.umpire === userName
        );

      setMatches(userMatches);
    };

    fetchMatches();
  }, [user]);

  const groupedMatches = matches.reduce((acc, match) => {
    if (!acc[match.date]) acc[match.date] = [];
    acc[match.date].push(match);
    return acc;
  }, {});

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4 text-black dark:text-white">Your Upcoming Games</h1>
      {Object.entries(groupedMatches).map(([date, dayMatches]) => (
        <div key={date} className="mb-6">
          <h2 className="text-lg font-semibold mb-2 text-gray-800 dark:text-gray-200">
            {dayjs(date).format("dddd, MMM D")}
          </h2>
          <div className="space-y-2">
            {dayMatches.map((match) => {
              const isUmpire = match.umpire === user.displayName;
              return (
                <div key={match.id} className="p-4 bg-white dark:bg-gray-700 rounded shadow">
                  <p className="text-sm text-gray-500 dark:text-gray-300">
                    üïí {match.time} ‚Äî üèüÔ∏è {match.venue}
                  </p>
                  <p className="font-medium text-black dark:text-white">
                    {match.team1} vs {match.team2}
                  </p>
                  {isUmpire ? (
                    <p className="text-sm text-yellow-600 dark:text-yellow-300 font-semibold">
                      You are umpiring this match
                    </p>
                  ) : (
                    <p className="text-sm text-blue-600 dark:text-blue-300">
                      Umpire: {match.umpire}
                    </p>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      ))}
      {matches.length === 0 && (
        <p className="text-gray-600 dark:text-gray-400">No upcoming games found.</p>
      )}
    </div>
  );
}
