import { useState, useEffect } from "react";
import { collection, collectionGroup, getDocs, doc, updateDoc } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";
import dayjs from "dayjs";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);
  const [loading, setLoading] = useState(true);
  const [matchesLoaded, setMatchesLoaded] = useState(false);
  const [umpiresLoaded, setUmpiresLoaded] = useState(false);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        let snapshot;
        if (currentTournament) {
          const matchesRef = collection(db, `tournaments/${currentTournament.id}/matches`);
          snapshot = await getDocs(matchesRef);
          const data = snapshot.docs.map(doc => ({ id: doc.id, tournamentId: currentTournament.id, ...doc.data() }));
          console.log("Fetched matches for current tournament:", data);
          setMatches(data);
        } else {
          const matchesRef = collectionGroup(db, "matches");
          snapshot = await getDocs(matchesRef);
          const data = snapshot.docs.map(doc => {
            const pathSegments = doc.ref.path.split('/');
            const tournamentId = pathSegments[1];
            return { id: doc.id, tournamentId, ...doc.data() };
          });
          console.log("Fetched matches across all tournaments:", data);
          setMatches(data);
        }
        setMatchesLoaded(true);
      } catch (error) {
        console.error("Error fetching matches:", error);
      }
    };

    const fetchUmpires = async () => {
      try {
        const usersRef = collection(db, "users");
        const snapshot = await getDocs(usersRef);
        const umpireList = snapshot.docs
          .map(doc => doc.data())
          .filter(user => user.role === "umpire")
          .map(user => ({
            name: `${user.firstName} ${user.lastName}`,
            email: user.email
          }));
        console.log("Fetched umpires:", umpireList);
        setUmpires(umpireList);
        setUmpiresLoaded(true);
      } catch (error) {
        console.error("Error fetching umpires:", error);
      }
    };

    fetchMatches();
    fetchUmpires();
  }, [currentTournament]);

  useEffect(() => {
    if (matchesLoaded && umpiresLoaded) {
      setLoading(false);
    }
  }, [matchesLoaded, umpiresLoaded]);

  if (loading) {
    return <div className="p-4 text-white">Loading matches and umpires...</div>;
  }

  if (!matches.length) {
    return <div className="p-4 text-white">No matches available to display.</div>;
  }

  const handleAssignUmpire = async (matchId, umpireName) => {
    try {
      // Find the tournamentId for this match from matches array
      const matchObj = matches.find(m => m.id === matchId);
      const tournamentId = matchObj?.tournamentId;
      if (!tournamentId) throw new Error("Tournament ID not found for match");
      const matchRef = doc(db, `tournaments/${tournamentId}/matches`, matchId);
      await updateDoc(matchRef, { umpire: umpireName });
      setMatches(prev =>
        prev.map(m => (m.id === matchId ? { ...m, umpire: umpireName } : m))
      );
    } catch (error) {
      console.error("Error assigning umpire:", error);
    }
  };

  const groupedMatches = matches
    .sort((a, b) => new Date(a.time) - new Date(b.time))
    .reduce((acc, match) => {
      const timeKey = dayjs(match.time).format("YYYY-MM-DD HH:mm"); // more consistent formatting
      if (!acc[timeKey]) acc[timeKey] = [];
      acc[timeKey].push(match);
      return acc;
    }, {});

  return (
    <div className="p-4 text-white">
      <h2 className="text-2xl font-bold underline mb-4">Umpire Appointments</h2>

      {Object.keys(groupedMatches).sort((a, b) => new Date(a) - new Date(b)).map(time => {
        const matchesAtTime = groupedMatches[time];
        const colCount = matchesAtTime.length;
        return (
          <div key={time} className="mb-8 border-b border-white pb-4">
            <h3 className="text-xl font-semibold mb-3">{dayjs(time).format("dddd, D MMM YYYY - HH:mm")}</h3>
            <div className={`grid grid-cols-${colCount} gap-4 mb-1`}>
              {matchesAtTime.map((match, idx) => (
                <div key={match.id || idx} className="bg-blue-700 p-2 rounded shadow-md min-h-[30px] flex items-center justify-center font-bold text-center">
                  {match.division || "N/A"}
                </div>
              ))}
            </div>
            <div className={`grid grid-cols-${colCount} gap-4 mb-1`}>
              {matchesAtTime.map((match, idx) => (
                <div key={match.id || idx} className="bg-blue-700 p-2 rounded shadow-md min-h-[30px] flex items-center justify-center text-center">
                  {match.teams ? match.teams.split(" vs ")[0] : "TBD"}
                </div>
              ))}
            </div>
            <div className={`grid grid-cols-${colCount} gap-4 mb-1`}>
              {matchesAtTime.map((match, idx) => (
                <div key={match.id || idx} className="bg-blue-700 p-2 rounded shadow-md min-h-[30px] flex items-center justify-center text-center">
                  {match.teams ? match.teams.split(" vs ")[1] : "TBD"}
                </div>
              ))}
            </div>
            <div className={`grid grid-cols-${colCount} gap-4`}>
              {matchesAtTime.map((match, idx) => (
                <div key={match.id || idx} className="bg-blue-700 p-2 rounded shadow-md min-h-[50px] flex flex-col items-center justify-center text-center">
                  <select
                    className="mt-1 p-1 w-full text-black"
                    onChange={e => handleAssignUmpire(match.id, e.target.value)}
                    value={match.umpire || ""}
                  >
                    <option value="">Assign Umpire</option>
                    {umpires.map(u => (
                      <option key={u.email} value={u.name}>{u.name}</option>
                    ))}
                  </select>
                  <p className="italic text-sm mt-1 w-full truncate">{match.umpire || "Unassigned"}</p>
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
}
