import { useState } from "react";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { db } from "../firebase";
import { useAuth } from "../context/AuthContext";

export default function MatchReportForm() {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    date: "",
    time: "",
    venue: "",
    division: "",
    yourTeam: "",
    opponent: "",
    suspectActions: "",
    feedback: "",
  });
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const docRef = {
      ...formData,
      submittedBy: user.uid,
      submittedAt: serverTimestamp(),
    };
    await addDoc(collection(db, "matchReports"), docRef);
    setSuccess(true);
    setFormData({
      date: "",
      time: "",
      venue: "",
      division: "",
      yourTeam: "",
      opponent: "",
      suspectActions: "",
      feedback: "",
    });
  };

  return (
    <div className="max-w-2xl mx-auto p-4 bg-white dark:bg-gray-800 shadow rounded">
      <h2 className="text-xl font-bold mb-4">Match Report</h2>
      {success && <p className="text-green-500 mb-4">Report submitted!</p>}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {["date", "time", "venue", "division", "yourTeam", "opponent"].map((field) => (
          <input
            key={field}
            name={field}
            value={formData[field]}
            onChange={handleChange}
            placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
            className="p-2 border rounded"
            required
          />
        ))}
        <textarea
          name="suspectActions"
          value={formData.suspectActions}
          onChange={handleChange}
          placeholder="Suspect Actions"
          className="col-span-full p-2 border rounded"
        />
        <textarea
          name="feedback"
          value={formData.feedback}
          onChange={handleChange}
          placeholder="Code of Conduct / General Feedback"
          className="col-span-full p-2 border rounded"
        />
        <button type="submit" className="col-span-full bg-blue-600 text-white py-2 rounded">
          Submit Report
        </button>
      </form>
    </div>
  );
}
