import { useState, useEffect } from "react";
import { collection, getDocs, doc, updateDoc, query, where, Timestamp } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";
import dayjs from "dayjs";

export default function UmpireAppointments() {
  const { currentTournament, tournaments, setCurrentTournament } = useTournament();
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [assigningUmpire, setAssigningUmpire] = useState(null);
  const [editingUmpire, setEditingUmpire] = useState(null);
  const [umpireEditForm, setUmpireEditForm] = useState({
    firstName: '',
    lastName: '',
    nickname: ''
  });
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    division: '',
    dateFrom: '',
    dateTo: '',
    umpireStatus: ''
  });
  useEffect(() => {
    if (!currentTournament && tournaments.length > 0) {
      // Try to auto-select the first tournament if none is selected
      setCurrentTournament(tournaments[0]);
    }
  }, [currentTournament, tournaments, setCurrentTournament]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        if (!currentTournament?.id) {
          setLoading(false);
          return;
        }

        let matchesQuery = collection(db, `tournaments/${currentTournament.id}/matches`);

        if (filters.dateFrom) {
          const fromDate = new Date(filters.dateFrom);
          fromDate.setHours(0, 0, 0, 0);
          matchesQuery = query(matchesQuery, where("date", ">=", Timestamp.fromDate(fromDate)));
        }

        if (filters.dateTo) {
          const toDate = new Date(filters.dateTo);
          toDate.setHours(23, 59, 59, 999);
          matchesQuery = query(matchesQuery, where("date", "<=", Timestamp.fromDate(toDate)));
        }

        if (filters.division) {
          matchesQuery = query(matchesQuery, where("division", "==", filters.division));
        }

        if (filters.umpireStatus === 'assigned') {
          matchesQuery = query(matchesQuery, where("umpireId", "!=", ""));
        } else if (filters.umpireStatus === 'unassigned') {
          matchesQuery = query(matchesQuery, where("umpireId", "==", ""));
        }

        const [matchesSnapshot, umpiresSnapshot] = await Promise.all([
          getDocs(matchesQuery),
          getDocs(query(collection(db, "users"), where("role", "==", "umpire")))
        ]);

        const matchesData = matchesSnapshot.docs.map(doc => {
          const data = doc.data();
          let date;

          try {
            date = data.date?.toDate?.() || data.date;
            if (date instanceof Timestamp) date = date.toDate();
          } catch (e) {
            console.error("Error parsing date:", e);
            date = new Date();
          }

          return {
            id: doc.id,
            ...data,
            date,
            time: data.time || "00:00"
          };
        });

        const umpiresData = umpiresSnapshot.docs.map(doc => ({
          id: doc.id,
          firstName: doc.data().firstName || '',
          lastName: doc.data().lastName || '',
          nickname: doc.data().nickname || null,
          ...doc.data()
        }));

        setMatches(matchesData);
        setUmpires(umpiresData);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [currentTournament?.id, filters]);

  const handleAssignUmpire = async (matchId, umpireId) => {
    try {
      setAssigningUmpire(matchId);
      const umpire = umpires.find(u => u.id === umpireId);
      if (!umpire || !currentTournament?.id) return;

      const displayName = umpire.nickname || `${umpire.firstName} ${umpire.lastName}`;

      await updateDoc(
        doc(db, `tournaments/${currentTournament.id}/matches`, matchId),
        { umpire: displayName, umpireId: umpire.id }
      );

      setMatches(prev => 
        prev.map(m => 
          m.id === matchId 
            ? { ...m, umpire: displayName, umpireId: umpire.id } 
            : m
        )
      );
    } catch (err) {
      console.error("Error assigning umpire:", err);
      setError("Failed to assign umpire");
    } finally {
      setAssigningUmpire(null);
    }
  };

  const handleEditUmpire = (umpire) => {
    setEditingUmpire(umpire);
    setUmpireEditForm({
      firstName: umpire.firstName,
      lastName: umpire.lastName,
      nickname: umpire.nickname || ''
    });
  };

  const handleUpdateUmpire = async () => {
    try {
      if (!editingUmpire) return;

      await updateDoc(doc(db, "users", editingUmpire.id), {
        firstName: umpireEditForm.firstName,
        lastName: umpireEditForm.lastName,
        nickname: umpireEditForm.nickname || null
      });

      setUmpires(prev => 
        prev.map(u => 
          u.id === editingUmpire.id 
            ? { 
                ...u, 
                firstName: umpireEditForm.firstName,
                lastName: umpireEditForm.lastName,
                nickname: umpireEditForm.nickname || null
              } 
            : u
        )
      );

      setEditingUmpire(null);
    } catch (err) {
      console.error("Error updating umpire:", err);
      setError("Failed to update umpire");
    }
  };

  const filteredUmpires = umpires.filter(umpire => {
    const searchLower = searchTerm.toLowerCase();
    const fullName = `${umpire.firstName} ${umpire.lastName}`.toLowerCase();
    const nickLower = umpire.nickname?.toLowerCase() || '';

    return (
      fullName.includes(searchLower) ||
      nickLower.includes(searchLower) ||
      umpire.id.toLowerCase().includes(searchLower)
    );
  });

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const resetFilters = () => {
    setFilters({
      division: '',
      dateFrom: '',
      dateTo: '',
      umpireStatus: ''
    });
  };

  if (loading && currentTournament) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-white">Loading matches for {currentTournament.name}...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4 bg-red-900 rounded text-white">
        <p className="font-bold">Error:</p>
        <p>{error}</p>
        <button 
          onClick={() => window.location.reload()}
          className="mt-2 px-4 py-2 bg-white text-red-900 rounded"
        >
          Retry
        </button>
      </div>
    );
  }

  if (!currentTournament) {
    return (
      <div className="p-4 max-w-2xl mx-auto">
        <h2 className="text-2xl font-bold text-white mb-6">Select Tournament</h2>
        <div className="bg-gray-800 p-6 rounded-lg">
          <label className="block text-white mb-2">Choose a tournament:</label>
          <select
            className="w-full p-3 rounded bg-white text-black"
            onChange={(e) => {
              const selected = tournaments.find(t => t.id === e.target.value);
              setCurrentTournament(selected || null);
            }}
          >
            <option value="">Select a tournament</option>
            {tournaments.map(t => (
              <option key={t.id} value={t.id}>{t.name}</option>
            ))}
          </select>
          <p className="text-gray-300 mt-4">
            Please select a tournament to view and assign umpires
          </p>
        </div>
      </div>
    );
  }

  const divisions = [...new Set(matches.map(match => match.division))];

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-white">
          Umpire Appointments for {currentTournament.name}
        </h2>
        <button
          onClick={() => setCurrentTournament(null)}
          className="px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-600"
        >
          Change Tournament
        </button>
      </div>

      {/* Umpire Management Section */}
      <div className="bg-gray-800 p-4 rounded-lg mb-6">
        <h3 className="text-lg font-semibold text-white mb-3">Umpire Management</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-white text-sm mb-1">Search Umpires</label>
            <input
              type="text"
              placeholder="Search by name or nickname..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full p-2 rounded bg-white text-black"
            />

            <div className="mt-4 max-h-60 overflow-y-auto">
              {filteredUmpires.map(umpire => (
                <div key={umpire.id} className="p-2 border-b border-gray-700 flex justify-between items-center">
                  <div>
                    <p className="text-white">
                      {umpire.nickname || `${umpire.firstName} ${umpire.lastName}`}
                    </p>
                    {umpire.nickname && (
                      <p className="text-gray-400 text-sm">
                        {umpire.firstName} {umpire.lastName}
                      </p>
                    )}
                  </div>
                  <button
                    onClick={() => handleEditUmpire(umpire)}
                    className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
                  >
                    Edit
                  </button>
                </div>
              ))}
            </div>
          </div>

          {editingUmpire && (
            <div>
              <h4 className="text-white mb-3">Edit Umpire</h4>
              <div className="space-y-3">
                <div>
                  <label className="block text-white text-sm mb-1">First Name</label>
                  <input
                    type="text"
                    value={umpireEditForm.firstName}
                    onChange={(e) => setUmpireEditForm(prev => ({ ...prev, firstName: e.target.value }))}
                    className="w-full p-2 rounded bg-white text-black"
                  />
                </div>
                <div>
                  <label className="block text-white text-sm mb-1">Last Name</label>
                  <input
                    type="text"
                    value={umpireEditForm.lastName}
                    onChange={(e) => setUmpireEditForm(prev => ({ ...prev, lastName: e.target.value }))}
                    className="w-full p-2 rounded bg-white text-black"
                  />
                </div>
                <div>
                  <label className="block text-white text-sm mb-1">Nickname (optional)</label>
                  <input
                    type="text"
                    value={umpireEditForm.nickname}
                    onChange={(e) => setUmpireEditForm(prev => ({ ...prev, nickname: e.target.value }))}
                    className="w-full p-2 rounded bg-white text-black"
                  />
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={handleUpdateUmpire}
                    className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
                  >
                    Save Changes
                  </button>
                  <button
                    onClick={() => setEditingUmpire(null)}
                    className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Filter Section */}
      <div className="bg-gray-800 p-4 rounded-lg mb-6">
        <h3 className="text-lg font-semibold text-white mb-3">Match Filters</h3>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
          <div>
            <label className="block text-white text-sm mb-1">Division</label>
            <select
              name="division"
              value={filters.division}
              onChange={handleFilterChange}
              className="w-full p-2 rounded bg-white text-black"
            >
              <option value="">All Divisions</option>
              {divisions.map(division => (
                <option key={division} value={division}>{division}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-white text-sm mb-1">From Date</label>
            <input
              type="date"
              name="dateFrom"
              value={filters.dateFrom}
              onChange={handleFilterChange}
              className="w-full p-2 rounded bg-white text-black"
            />
          </div>
          <div>
            <label className="block text-white text-sm mb-1">To Date</label>
            <input
              type="date"
              name="dateTo"
              value={filters.dateTo}
              onChange={handleFilterChange}
              className="w-full p-2 rounded bg-white text-black"
            />
          </div>
          <div>
            <label className="block text-white text-sm mb-1">Umpire Status</label>
            <select
              name="umpireStatus"
              value={filters.umpireStatus}
              onChange={handleFilterChange}
              className="w-full p-2 rounded bg-white text-black"
            >
              <option value="">All</option>
              <option value="assigned">Assigned</option>
              <option value="unassigned">Unassigned</option>
            </select>
          </div>
          <div className="flex items-end">
            <button
              onClick={resetFilters}
              className="px-4 py-2 bg-gray-700 text-white rounded hover:bg-gray-600 w-full"
            >
              Reset Filters
            </button>
          </div>
        </div>
      </div>

      {matches.length === 0 ? (
        <div className="bg-gray-800 p-6 rounded-lg text-white">
          <p>No matches found matching your criteria.</p>
          <button
            onClick={resetFilters}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            Clear Filters
          </button>
        </div>
      ) : (
        <>
          {Object.entries(
            matches.reduce((acc, match) => {
              const dateKey = dayjs(match.date).format("YYYY-MM-DD");
              if (!acc[dateKey]) acc[dateKey] = [];
              acc[dateKey].push(match);
              return acc;
            }, {})
          )
            .sort(([dateA], [dateB]) => new Date(dateA) - new Date(dateB))
            .map(([date, dateMatches]) => (
              <div key={date} className="mb-8 bg-gray-800 rounded-lg overflow-hidden">
                <div className="bg-gray-700 p-3">
                  <h3 className="text-xl font-semibold text-white">
                    {dayjs(date).format("dddd, D MMMM YYYY")}
                  </h3>
                </div>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-700">
                      <tr>
                        <th className="p-3 text-left text-white">Time</th>
                        <th className="p-3 text-left text-white">Division</th>
                        <th className="p-3 text-left text-white">Court</th>
                        <th className="p-3 text-left text-white">Home vs Away</th>
                        <th className="p-3 text-left text-white">Umpire</th>
                      </tr>
                    </thead>
                    <tbody>
                      {dateMatches
                        .sort((a, b) => a.time.localeCompare(b.time))
                        .map((match) => (
                          <tr key={match.id} className="border-t border-gray-700 hover:bg-gray-750">
                            <td className="p-3 text-white">{match.time}</td>
                            <td className="p-3 text-white">{match.division}</td>
                            <td className="p-3 text-white">{match.court || 'TBD'}</td>
                            <td className="p-3 text-white">
                              {match.home || 'TBD'} vs {match.away || 'TBD'}
                            </td>
                            <td className="p-3">
                              <select
                                className="w-full p-2 rounded bg-white text-black"
                                value={match.umpireId || ""}
                                onChange={(e) => handleAssignUmpire(match.id, e.target.value)}
                                disabled={assigningUmpire === match.id}
                              >
                                {assigningUmpire === match.id ? (
                                  <option>Assigning...</option>
                                ) : (
                                  <>
                                    <option value="">Select Umpire</option>
                                    {umpires.map((umpire) => (
                                      <option key={umpire.id} value={umpire.id}>
                                        {umpire.nickname || `${umpire.firstName} ${umpire.lastName}`}
                                      </option>
                                    ))}
                                  </>
                                )}
                              </select>
                              {match.umpire && (
                                <div className="text-sm text-gray-300 mt-1">
                                  Current: {match.umpire}
                                </div>
                              )}
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
              </div>
            ))}
        </>
      )}
    </div>
  );
}
