import { useEffect, useState } from "react";
import { collection, getDocs, query, orderBy } from "firebase/firestore";
import { db } from "../firebase";
import { useAuth } from "../context/AuthContext";
import dayjs from "dayjs";

export default function Agenda() {
  const { user } = useAuth();
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchMatches = async () => {
      const q = query(collection(db, "tournaments/current/matches"), orderBy("date"), orderBy("time"));
      const snapshot = await getDocs(q);
      const result = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setMatches(result);
    };

    fetchMatches();
  }, []);

  const fullName = `${user?.firstName} ${user?.lastName}`;

  const relevantMatches = matches.filter(
    m =>
      m.team1 === fullName ||
      m.team2 === fullName ||
      m.umpire === fullName
  );

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <h1 className="text-xl font-bold mb-4 text-black dark:text-white">Your Match Agenda</h1>
      {relevantMatches.length === 0 && (
        <p className="text-gray-700 dark:text-gray-300">No matches found for your name.</p>
      )}
      <ul className="space-y-4">
        {relevantMatches.map(match => (
          <li key={match.id} className="border rounded p-4 bg-white dark:bg-gray-700 shadow">
            <div className="text-md font-semibold dark:text-white">
              {dayjs(match.date).format("dddd, MMM D")} at {match.time}
            </div>
            <div className="text-sm dark:text-gray-300">
              <strong>Division:</strong> {match.division}<br />
              <strong>Teams:</strong> {match.team1} vs {match.team2}<br />
              <strong>Umpire:</strong> {match.umpire}<br />
              <strong>Venue:</strong> {match.venue}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
