import { Link } from "react-router-dom";
import { useState } from "react";
import { LogOut, Sun, Moon, Home, FileText, ShieldCheck, BarChart2, Users, Upload } from "lucide-react";

export default function Sidebar({ role, onToggleTheme, darkMode }) {
  const [open, setOpen] = useState(true);

  const commonLinks = [
    { label: "Dashboard", to: "/dashboard", icon: <Home size={20} /> },
    { label: "Match Report", to: "/match-report", icon: <FileText size={20} /> },
    { label: "Umpire Report", to: "/umpire-report", icon: <ShieldCheck size={20} /> },
  ];

  const adminLinks = [
    { label: "Analytics", to: "/analytics", icon: <BarChart2 size={20} /> },
    { label: "Manage Users", to: "/admin/users", icon: <Users size={20} /> },
    { label: "Upload Draw", to: "/admin/upload", icon: <Upload size={20} /> },
  ];

  const links = role === "admin" ? [...commonLinks, ...adminLinks] : commonLinks;

  return (
    <div className={`flex flex-col h-full bg-gray-100 dark:bg-gray-900 transition-all ${open ? "w-64" : "w-16"} shadow-md`}>
      <div className="flex items-center justify-center p-4">
        <img src="/logo.png" alt="Indoor Cricket" className="h-16 rounded shadow-md" />
      </div>

      <button
        onClick={() => setOpen(!open)}
        className="p-2 text-sm self-end mx-2 mb-4 bg-gray-300 dark:bg-gray-700 rounded"
      >
        {open ? "←" : "→"}
      </button>

      <ul className="space-y-1 px-2">
        {links.map((link) => (
          <li key={link.to}>
            <Link
              to={link.to}
              className="flex items-center gap-3 p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              {link.icon}
              {open && <span>{link.label}</span>}
            </Link>
          </li>
        ))}
      </ul>

      <div className="mt-auto mb-4 flex flex-col gap-2 items-center">
        <button onClick={onToggleTheme} className="p-2 hover:scale-110">
          {darkMode ? <Sun /> : <Moon />}
        </button>
        <button onClick={() => alert("Sign out logic here")} className="p-2 hover:scale-110">
          <LogOut />
        </button>
      </div>
    </div>
  );
}
