import { useState, useEffect } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matchesByTime, setMatchesByTime] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      if (!currentTournament) return;
      try {
        const snapshot = await getDocs(collection(db, `tournaments/${currentTournament}/matches`));
        const matches = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));

        const grouped = matches.reduce((acc, match) => {
          const time = match.time || "Unknown";
          if (!acc[time]) acc[time] = [];
          acc[time].push(match);
          return acc;
        }, {});

        setMatchesByTime(grouped);
        setError(null);
      } catch (err) {
        console.error("Error fetching matches:", err);
        setError("Error fetching matches. Please check permissions and Firestore rules.");
      }
    };

    fetchMatches();
  }, [currentTournament]);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold underline mb-4 text-white">Umpire Appointments</h2>
      {error && <p className="text-red-500 font-semibold">{error}</p>}
      {Object.keys(matchesByTime).length === 0 ? (
        <p className="text-yellow-400 font-medium mt-4">No matches found in the selected tournament.</p>
      ) : (
        Object.entries(matchesByTime).map(([time, matches]) => (
          <div key={time} className="mb-8">
            <h3 className="text-xl font-bold text-white mb-2">{time}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {matches.map(match => (
                <div key={match.id} className="bg-blue-600 p-4 rounded text-white shadow-md">
                  <p className="font-bold">Court {match.court || "N/A"}</p>
                  <p>Type: {match.division || "N/A"}</p>
                  <p>{match.teams || "TBD vs TBD"}</p>
                  <p><em>Umpire: {match.umpire || "Unassigned"}</em></p>
                  <p className="text-xs mt-1 italic">Match ID: {match.id}</p>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
