import { useEffect, useState } from "react";
import { db } from "../firebase";
import {
  collection,
  getDocs,
  updateDoc,
  doc,
  query,
  orderBy,
} from "firebase/firestore";

export default function UmpireAppointments() {
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const matchesSnap = await getDocs(query(collection(db, "matches"), orderBy("date")));
      const usersSnap = await getDocs(collection(db, "users"));

      const umpireList = [];
      usersSnap.forEach((doc) => {
        const data = doc.data();
        if (data.role === "umpire") {
          umpireList.push({ id: doc.id, name: `${data.firstName} ${data.lastName}` });
        }
      });

      const matchList = [];
      matchesSnap.forEach((doc) => {
        const data = doc.data();
        matchList.push({ id: doc.id, ...data });
      });

      setUmpires(umpireList);
      setMatches(matchList);
      setLoading(false);
    };

    fetchData();
  }, []);

  const handleChange = async (matchId, newUmpireId) => {
    await updateDoc(doc(db, "matches", matchId), { umpire: newUmpireId });
    setMatches((prev) =>
      prev.map((match) =>
        match.id === matchId ? { ...match, umpire: newUmpireId } : match
      )
    );
  };

  const formatDate = (timestamp) => {
    if (timestamp?.seconds) {
      return new Date(timestamp.seconds * 1000).toLocaleDateString();
    }
    return timestamp;
  };

  const formatTime = (timestamp) => {
    if (timestamp?.seconds) {
      return new Date(timestamp.seconds * 1000).toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      });
    }
    return timestamp;
  };

  if (loading) return <p className="p-4">Loading...</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Umpire Appointments</h2>
      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200 dark:bg-gray-700">
            <th className="p-2 border">Date</th>
            <th className="p-2 border">Time</th>
            <th className="p-2 border">Venue</th>
            <th className="p-2 border">Team A</th>
            <th className="p-2 border">Team B</th>
            <th className="p-2 border">Umpire</th>
          </tr>
        </thead>
        <tbody>
          {matches.map((match) => (
            <tr key={match.id} className="text-center">
              <td className="border p-1">{formatDate(match.date)}</td>
              <td className="border p-1">{formatTime(match.time)}</td>
              <td className="border p-1">{match.venue}</td>
              <td className="border p-1">{match.teamA}</td>
              <td className="border p-1">{match.teamB}</td>
              <td className="border p-1">
                <select
                  value={match.umpire || ""}
                  onChange={(e) => handleChange(match.id, e.target.value)}
                  className="bg-white dark:bg-gray-700 border p-1"
                >
                  <option value="">Unassigned</option>
                  {umpires.map((u) => (
                    <option key={u.id} value={u.id}>
                      {u.name}
                    </option>
                  ))}
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
