import { useState, useEffect } from "react";
import { collection, getDocs, doc, updateDoc } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";
import dayjs from "dayjs";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);

  useEffect(() => {
    const fetchMatches = async () => {
      if (!currentTournament) return;
      try {
        const matchesRef = collection(db, `tournaments/${currentTournament}/matches`);
        const snapshot = await getDocs(matchesRef);
        const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setMatches(data);
      } catch (error) {
        console.error("Error fetching matches:", error);
      }
    };

    const fetchUmpires = async () => {
      try {
        const usersRef = collection(db, "users");
        const snapshot = await getDocs(usersRef);
        const umpireList = snapshot.docs
          .map(doc => doc.data())
          .filter(user => user.role === "umpire")
          .map(user => ({
            name: `${user.firstName} ${user.lastName}`,
            email: user.email
          }));
        setUmpires(umpireList);
      } catch (error) {
        console.error("Error fetching umpires:", error);
      }
    };

    fetchMatches();
    fetchUmpires();
  }, [currentTournament]);

  const handleAssignUmpire = async (matchId, umpireName) => {
    try {
      const matchRef = doc(db, `tournaments/${currentTournament}/matches`, matchId);
      await updateDoc(matchRef, { umpire: umpireName });
      setMatches(prev =>
        prev.map(m => (m.id === matchId ? { ...m, umpire: umpireName } : m))
      );
    } catch (error) {
      console.error("Error assigning umpire:", error);
    }
  };

  const groupedMatches = matches
    .sort((a, b) => new Date(a.time) - new Date(b.time))
    .reduce((acc, match) => {
      const time = match.time || "Unknown Time";
      if (!acc[time]) acc[time] = [];
      acc[time].push(match);
      return acc;
    }, {});

  const courts = ["Court 1", "Court 2", "Court 3", "Court 4"];

  return (
    <div className="p-4 text-white">
      <h2 className="text-2xl font-bold underline mb-4">Umpire Appointments</h2>

      {Object.keys(groupedMatches).sort((a, b) => new Date(a) - new Date(b)).map(time => (
        <div key={time} className="mb-8 border-b border-white pb-4">
          <h3 className="text-xl font-semibold mb-3">{dayjs(time).format("dddd, HH:mm")}</h3>
          <div className="grid grid-cols-4 gap-4">
            {courts.map(court => {
              const match = groupedMatches[time].find(m => m.court === court);
              return (
                <div key={court} className="bg-blue-700 p-4 rounded shadow-md min-h-[150px]">
                  <p className="font-bold mb-1">{court}</p>
                  <p className="text-sm">Type: {match?.division || "N/A"}</p>
                  <p className="text-sm">{match?.teams || "TBD vs TBD"}</p>
                  <p className="italic text-sm">Umpire: {match?.umpire || "Unassigned"}</p>
                  {match && (
                    <select
                      className="mt-2 p-1 w-full text-black"
                      onChange={e => handleAssignUmpire(match.id, e.target.value)}
                      value={match.umpire || ""}
                    >
                      <option value="">Assign Umpire</option>
                      {umpires.map(u => (
                        <option key={u.email} value={u.name}>{u.name}</option>
                      ))}
                    </select>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
}
