import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { auth, db } from "../firebase";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [state, setState] = useState("");
  const [position, setPosition] = useState("");
  const [error, setError] = useState("");
  const [isSignup, setIsSignup] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      if (isSignup) {
        const userCred = await createUserWithEmailAndPassword(auth, email, password);
        await setDoc(doc(db, "users", userCred.user.uid), {
          email: userCred.user.email,
          role: "unassigned",
          firstName,
          lastName,
          phone,
          state,
          position,
        });
      } else {
        await signInWithEmailAndPassword(auth, email, password);
      }
      navigate("/dashboard");
    } catch (err) {
      setError(err.message);
    }
  };

  const stateOptions = ["NSW", "VIC", "QLD", "SA", "WA", "TAS", "ACT", "NT"];
  const positionOptions = ["Umpire", "Team Manager", "Coach", "Player"];

  return (
    <div className="max-w-sm mx-auto mt-20 p-4 bg-white dark:bg-gray-800 shadow rounded">
      <h2 className="text-xl font-bold mb-4">{isSignup ? "Create Account" : "Login"}</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        {isSignup && (
          <>
            <input className="w-full p-2 border rounded" placeholder="First Name" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
            <input className="w-full p-2 border rounded" placeholder="Last Name" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
            <input className="w-full p-2 border rounded" placeholder="Phone Number" value={phone} onChange={(e) => setPhone(e.target.value)} required />
            <select className="w-full p-2 border rounded" value={state} onChange={(e) => setState(e.target.value)} required>
              <option value="">Select State</option>
              {stateOptions.map((s) => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
            <select className="w-full p-2 border rounded" value={position} onChange={(e) => setPosition(e.target.value)} required>
              <option value="">Select Position</option>
              {positionOptions.map((p) => (
                <option key={p} value={p}>{p}</option>
              ))}
            </select>
          </>
        )}
        <input className="w-full p-2 border rounded" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input className="w-full p-2 border rounded" placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">
          {isSignup ? "Sign Up" : "Login"}
        </button>
        <button type="button" onClick={() => setIsSignup(!isSignup)} className="text-sm text-blue-500 underline">
          {isSignup ? "Already have an account? Login" : "New user? Sign up"}
        </button>
        {error && <p className="text-red-500 text-sm">{error}</p>}
      </form>
    </div>
  );
}
