import { useState, useEffect } from "react";
import { collection, getDocs, doc, updateDoc, query, where } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";
import dayjs from "dayjs";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matches, setMatches] = useState([]);
  const [umpires, setUmpires] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch matches for current tournament
        const matchesQuery = query(
          collection(db, `tournaments/${currentTournament?.id}/matches`),
          where("date", ">=", new Date().toISOString().split('T')[0]) // Only future matches
        );
        const matchesSnapshot = await getDocs(matchesQuery);
        const matchesData = matchesSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
          date: doc.data().date?.toDate?.() || doc.data().date,
          time: doc.data().time || "00:00" // Default time if missing
        }));

        // Fetch umpires
        const usersQuery = query(
          collection(db, "users"),
          where("role", "==", "umpire")
        );
        const usersSnapshot = await getDocs(usersQuery);
        const umpiresData = usersSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data(),
          fullName: `${doc.data().firstName} ${doc.data().lastName}`
        }));

        setMatches(matchesData);
        setUmpires(umpiresData);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError("Failed to load data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    if (currentTournament?.id) {
      fetchData();
    }
  }, [currentTournament]);

  const handleAssignUmpire = async (matchId, umpireId) => {
    try {
      const umpire = umpires.find(u => u.id === umpireId);
      if (!umpire) return;

      const matchRef = doc(db, `tournaments/${currentTournament.id}/matches`, matchId);
      await updateDoc(matchRef, {
        umpire: umpire.fullName,
        umpireId: umpire.id
      });

      setMatches(prev =>
        prev.map(m => 
          m.id === matchId 
            ? { ...m, umpire: umpire.fullName, umpireId: umpire.id } 
            : m
        )
      );
    } catch (err) {
      console.error("Error assigning umpire:", err);
      setError("Failed to assign umpire");
    }
  };

  // Group matches by date
  const matchesByDate = matches.reduce((acc, match) => {
    const dateKey = dayjs(match.date).format("YYYY-MM-DD");
    if (!acc[dateKey]) acc[dateKey] = [];
    acc[dateKey].push(match);
    return acc;
  }, {});

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="text-white">Loading matches and umpires...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4 text-red-500 bg-red-100 rounded">
        {error}
      </div>
    );
  }

  if (!currentTournament) {
    return (
      <div className="p-4 text-yellow-500 bg-yellow-100 rounded">
        Please select a tournament first
      </div>
    );
  }

  if (matches.length === 0) {
    return (
      <div className="p-4 text-white">
        No upcoming matches found for {currentTournament.name}
      </div>
    );
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold text-white mb-6">Umpire Appointments</h2>

      {Object.entries(matchesByDate)
        .sort(([dateA], [dateB]) => new Date(dateA) - new Date(dateB))
        .map(([date, dateMatches]) => (
          <div key={date} className="mb-8">
            <h3 className="text-xl font-semibold text-white mb-4">
              {dayjs(date).format("dddd, D MMMM YYYY")}
            </h3>

            <div className="overflow-x-auto">
              <table className="w-full bg-white bg-opacity-10 rounded-lg">
                <thead>
                  <tr className="border-b border-white border-opacity-20">
                    <th className="p-3 text-left text-white">Time</th>
                    <th className="p-3 text-left text-white">Division</th>
                    <th className="p-3 text-left text-white">Teams</th>
                    <th className="p-3 text-left text-white">Umpire</th>
                  </tr>
                </thead>
                <tbody>
                  {dateMatches
                    .sort((a, b) => a.time.localeCompare(b.time))
                    .map((match) => (
                      <tr 
                        key={match.id} 
                        className="border-b border-white border-opacity-10 hover:bg-white hover:bg-opacity-5"
                      >
                        <td className="p-3 text-white">
                          {match.time || "TBD"}
                        </td>
                        <td className="p-3 text-white">
                          {match.division || "N/A"}
                        </td>
                        <td className="p-3 text-white">
                          {match.teams || "TBD vs TBD"}
                        </td>
                        <td className="p-3">
                          <select
                            className="w-full p-2 rounded bg-white text-black"
                            value={match.umpireId || ""}
                            onChange={(e) => handleAssignUmpire(match.id, e.target.value)}
                          >
                            <option value="">Select Umpire</option>
                            {umpires.map((umpire) => (
                              <option key={umpire.id} value={umpire.id}>
                                {umpire.fullName}
                              </option>
                            ))}
                          </select>
                          {match.umpire && (
                            <div className="text-sm text-white mt-1">
                              Currently assigned: {match.umpire}
                            </div>
                          )}
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          </div>
        ))}
    </div>
  );
}
