import { useEffect, useState } from "react";
import { collection, getDocs, query, where } from "firebase/firestore";
import { db } from "../firebase";
import dayjs from "dayjs";
import TournamentSelector from "../components/TournamentSelector";
import { useTournament } from "../context/TournamentContext";
import { useAuth } from "../context/AuthContext";
import { doc, updateDoc } from "firebase/firestore";

export default function UmpireAppointments() {
  const [matches, setMatches] = useState([]);
  const [selectedDate, setSelectedDate] = useState(dayjs().format("YYYY-MM-DD"));
  const [viewMode, setViewMode] = useState("day"); // or "tournament"
  const { currentTournament, setCurrentTournament } = useTournament();

  const { role, user } = useAuth();
  const isAdmin = role === "admin";
  const [editingMatchId, setEditingMatchId] = useState(null);
  const [newUmpireName, setNewUmpireName] = useState("");

  useEffect(() => {
    if (!currentTournament) return;
    const fetchMatches = async () => {
      const matchesRef = collection(db, `tournaments/${currentTournament}/matches`);
      const q = viewMode === "day"
        ? query(matchesRef, where("date", "==", selectedDate))
        : matchesRef;

      const snapshot = await getDocs(q);
      const data = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setMatches(data);
    };
    fetchMatches();
  }, [selectedDate, viewMode, currentTournament]);

  const times = [...new Set(matches.map(match => match.time))].sort();
  const courts = ["Court 1", "Court 2", "Court 3", "Court 4"];

  const matchesByTimeAndCourt = times.map(time => {
    const matchesAtTime = matches.filter(match => match.time === time);
    const courtMap = {};
    courts.forEach(court => {
      courtMap[court] = matchesAtTime.find(match => match.court === court);
    });
    return { time, courtMap };
  });

  const handleUmpireChange = async (matchId) => {
    if (!newUmpireName || !currentTournament) return;
    const matchRef = doc(db, `tournaments/${currentTournament}/matches/${matchId}`);
    await updateDoc(matchRef, { umpireName: newUmpireName });
    setEditingMatchId(null);
    setNewUmpireName("");
    setSelectedDate(prev => prev); // Refresh
  };

  return (
    <div className="p-4 text-gray-900 dark:text-gray-100">
      <h1 className="text-2xl mx-auto font-bold mb-4">Umpire Appointments</h1>

      <div className="flex flex-wrap gap-4 mb-4 items-center">
        <TournamentSelector onSelect={setCurrentTournament} />

        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          className="p-2 border rounded bg-white dark:bg-gray-900 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
        />

        <select
          value={viewMode}
          onChange={(e) => setViewMode(e.target.value)}
          className="p-2 border rounded bg-white dark:bg-gray-900 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-400"
        >
          <option value="day">Day View</option>
          <option value="tournament">Tournament View</option>
        </select>
      </div>

      {matchesByTimeAndCourt.map(({ time, courtMap }) => (
        <div key={time} className="mb-6">
          <h2 className="text-lg font-semibold mb-2">{time}</h2>
          <div className="grid grid-cols-4 gap-4">
            {courts.map(court => {
              const match = courtMap[court];
              return (
                <div key={court} className="ml-1 px-2 py-0.5 text-xs bg-blue-600 text-white rounded">
                  <p className="font-semibold">{court}</p>
                  {match ? (
                    <>
                      <p>{match.type}</p>
                      <p>{match.team1} vs {match.team2}</p>
                      <p className="italic">
                        Umpire:{" "}
                        {isAdmin && editingMatchId === match.id ? (
                          <>
                            <input
                              value={newUmpireName}
                              onChange={(e) => setNewUmpireName(e.target.value)}
                              className="text-black px-1 py-0.5 rounded"
                            />
                            <button
                              onClick={() => handleUmpireChange(match.id)}
                              className="ml-1 px-2 py-0.5 text-xs bg-blue-600 text-white rounded"
                            >
                              Save
                            </button>
                          </>
                        ) : (
                          <>
                            {match.umpireName}{" "}
                            {isAdmin && (
                              <button
                                onClick={() => {
                                  setEditingMatchId(match.id);
                                  setNewUmpireName(match.umpireName || "");
                                }}
                                className="ml-1 text-xs text-blue-400 underline"
                              >
                                Edit
                              </button>
                            )}
                          </>
                        )}
                      </p>
                    </>
                  ) : (
                    <p className="italic text-gray-500">No Match Scheduled</p>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
}
