import { useState, useEffect } from "react";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../firebase";
import { useTournament } from "../context/TournamentContext";

export default function UmpireAppointments() {
  const { currentTournament } = useTournament();
  const [matchesByTime, setMatchesByTime] = useState({});

  useEffect(() => {
    const fetchMatches = async () => {
      if (!currentTournament) {
        setMatchesByTime({});
        return;
      }
      try {
        const matchesCollection = collection(db, `tournaments/${currentTournament}/matches`);
        const snapshot = await getDocs(matchesCollection);
        const matches = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));

        const grouped = matches.reduce((acc, match) => {
          let timeStr = match.time || "Unknown Time";
          if (!acc[timeStr]) acc[timeStr] = [];
          acc[timeStr].push(match);
          return acc;
        }, {});
        setMatchesByTime(grouped);
      } catch (error) {
        console.error("Error fetching matches: ", error);
        setMatchesByTime({});
      }
    };

    fetchMatches();
  }, [currentTournament]);

  return (
    <div>
      <h2 className="text-2xl font-bold underline mb-4 text-white">Umpire Appointments</h2>
      {Object.keys(matchesByTime).length === 0 ? (
        <p className="text-white mt-4">No appointments available.</p>
      ) : (
        Object.entries(matchesByTime).map(([timeSlot, matchesAtTime]) => (
          <div key={timeSlot}>
            <h3 className="text-lg font-bold text-white mt-6 mb-2">{timeSlot}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {matchesAtTime.map(match => (
                <div key={match.id} className="bg-blue-600 p-4 rounded shadow-md">
                  <div className="text-white text-sm">
                    <p className="font-semibold">Court: {match.court || "N/A"}</p>
                    <p>Division: {match.division || "N/A"}</p>
                    <p>Teams: {match.teams || "TBD vs TBD"}</p>
                    <p className="italic">Umpire: {match.umpire || "Unassigned"}</p>
                    <p className="text-xs mt-2">Match ID: {match.id}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
}
