import { useState } from "react";
import Papa from "papaparse";
import { db } from "../firebase";
import { collection, addDoc } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";

export default function UploadDraw() {
  const { role } = useAuth();
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setStatus("");
  };

  const handleUpload = () => {
    if (!file) {
      setStatus("Please select a CSV file first.");
      return;
    }

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: async (results) => {
        const matches = results.data;
        let successCount = 0;

        for (let match of matches) {
          if (match.date && match.time && match.team1 && match.team2 && match.umpire && match.venue) {
            try {
              await addDoc(collection(db, "matches"), {
                date: match.date,
                time: match.time,
                division: match.division || "",
                team1: match.team1,
                team2: match.team2,
                umpire: match.umpire,
                venue: match.venue
              });
              successCount++;
            } catch (error) {
              console.error("Error uploading match:", error);
            }
          }
        }

        setStatus(`${successCount} matches uploaded successfully.`);
      },
      error: (err) => {
        setStatus("Error parsing CSV: " + err.message);
      }
    });
  };

  if (role !== "admin") {
    return <div className="p-4 text-red-500">Access denied. Admins only.</div>;
  }

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h1 className="text-xl font-bold mb-4 text-black dark:text-white">Upload Match Draw</h1>
      <input type="file" accept=".csv" onChange={handleFileChange} className="mb-4" />
      <button
        onClick={handleUpload}
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Upload
      </button>
      {status && <p className="mt-4 text-sm text-gray-700 dark:text-gray-300">{status}</p>}
    </div>
  );
}
