function Agenda() {
  const { currentTournament } = useTournament();
  const [agendaItems, setAgendaItems] = useState([]);

  useEffect(() => {
    // Your existing agenda data fetching logic here
    // Can use currentTournament.id if needed
  }, [currentTournament]);

  return (
    <div className="p-4">
      <div className="mb-6">
        <h1 className="text-2xl font-bold mb-2">Tournament Agenda</h1>
        {currentTournament && (
          <div className="flex items-center gap-4">
            <h2 className="text-xl">{currentTournament.name}</h2>
            <span className="text-sm text-gray-500">
              {new Date(currentTournament.date?.toDate()).toLocaleDateString()}
            </span>
          </div>
        )}
      </div>

      {/* Sample agenda content - replace with yours */}
      <div className="space-y-4">
        {agendaItems.map(item => (
          <div key={item.id} className="p-4 border rounded-lg">
            <h3 className="font-medium">{item.matchName}</h3>
            <p>{item.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
