import { Link } from "react-router-dom";
import { useState } from "react";
import {
  LogOut, Sun, Moon, CalendarCheck2, Calendar, ScrollText,
  Scroll, LucideGaugeCircle, Settings, Info,
  PersonStanding, HardDriveUpload, ChevronDown, ChevronUp
} from "lucide-react";
import { signOut } from "firebase/auth";
import { auth } from "../firebase";

export default function Sidebar({ role, onToggleTheme, darkMode }) {
  const [open, setOpen] = useState(true);
  const [adminOpen, setAdminOpen] = useState(false);
  const [settingsOpen, setSettingsOpen] = useState(false);

  const commonLinks = [
    { label: "Dashboard", to: "/dashboard", icon: <LucideGaugeCircle /> },
    { label: "Agenda", to: "/agenda", icon: <Calendar /> },
    { label: "Match Report", to: "/match-report", icon: <ScrollText /> },
    { label: "Umpire Report", to: "/umpire-report", icon: <Scroll /> },
  ];

  const adminLinks = [
    { label: "Analytics", to: "/analytics", icon: <Info /> },
    { label: "Manage Users", to: "/admin/users", icon: <PersonStanding /> },
    { label: "Upload Draw", to: "/admin/upload", icon: <HardDriveUpload /> },
    { label: "Umpire Appointments", to: "/admin/appointments", icon: <CalendarCheck2 /> },
  ];
  const settingsLinks = [
    { label: "Tournament Admin", to: "/settings/tournamentadmin", icon: <Settings /> },
    { label: "My Details", to: "/settings/details", icon: <Moon /> },
  ];

  const renderLinks = (links) =>
    links.map((link) => (
      <li key={link.to}>
        <Link to={link.to} className="flex items-center gap-2 p-2 rounded hover:bg-gray-300 dark:hover:bg-gray-700">
          {link.icon}
          {open && link.label}
        </Link>
      </li>
    ));

  return (
    <div className={`flex flex-col h-full bg-gray-100 dark:bg-gray-800 transition-all ${open ? "w-60" : "w-16"}`}>
      <div className="flex items-center justify-center p-2">
        <img src="/logo.png" alt="Indoor Cricket" className="h-12 object-contain" />
      </div>
      <button onClick={() => setOpen(!open)} className="p-2 text-sm self-end m-2 bg-gray-300 dark:bg-gray-600 rounded">
        {open ? "←" : "→"}
      </button>

      <ul className="space-y-1 px-2">
        {renderLinks(commonLinks)}

        {role === "admin" && (
          <>
            <li>
              <button
                onClick={() => setAdminOpen(!adminOpen)}
                className="flex items-center justify-between w-full p-2 rounded bg-gray-200 dark:bg-gray-700"
              >
                <span className="flex items-center gap-2">
                  <Settings />
                  {open && "Admin"}
                </span>
                {open && (adminOpen ? <ChevronUp size={16} /> : <ChevronDown size={16} />)}
              </button>
            </li>
            {adminOpen && renderLinks(adminLinks)}
          </>
        )}

        <li>
          <button
            onClick={() => setSettingsOpen(!settingsOpen)}
            className="flex items-center justify-between w-full p-2 rounded bg-gray-200 dark:bg-gray-700"
          >
            <span className="flex items-center gap-2">
              <Settings />
              {open && "Settings"}
            </span>
            {open && (settingsOpen ? <ChevronUp size={16} /> : <ChevronDown size={16} />)}
          </button>
        </li>
        {settingsOpen && renderLinks(settingsLinks)}
      </ul>

      <div className="mt-auto mb-4 flex flex-col gap-2 items-center">
        <button onClick={onToggleTheme} className="p-2 hover:scale-110">
          {darkMode ? <Sun /> : <Moon />}
        </button>
        <button
          onClick={() => signOut(auth).then(() => (window.location.href = "/login"))}
          className="p-2 hover:scale-110"
        >
          <LogOut />
        </button>
      </div>
    </div>
  );
}
