import { useState } from "react";
import { db } from "../firebase";
import { collection, addDoc, Timestamp } from "firebase/firestore";

export default function UmpireReportForm() {
  const [teamOne, setTeamOne] = useState("");
  const [teamTwo, setTeamTwo] = useState("");
  const [comments, setComments] = useState("");
  const [spiritOne, setSpiritOne] = useState("1");
  const [spiritTwo, setSpiritTwo] = useState("1");
  const [status, setStatus] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Submitting...");
    try {
      await addDoc(collection(db, "umpireReports"), {
        teamOne,
        teamTwo,
        spiritOne: Number(spiritOne),
        spiritTwo: Number(spiritTwo),
        comments,
        submittedAt: Timestamp.now(),
      });
      setStatus("✅ Report submitted successfully");
      setTeamOne("");
      setTeamTwo("");
      setSpiritOne("1");
      setSpiritTwo("1");
      setComments("");
    } catch (err) {
      console.error(err);
      setStatus("❌ Failed to submit report");
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold underline mb-4">Umpire Report</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Team One"
          value={teamOne}
          onChange={(e) => setTeamOne(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="text"
          placeholder="Team Two"
          value={teamTwo}
          onChange={(e) => setTeamTwo(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />

        <div>
          <label className="block text-sm font-medium mb-1">Spirit of Cricket — {teamOne || "Team One"}</label>
          <select
            value={spiritOne}
            onChange={(e) => setSpiritOne(e.target.value)}
            className="w-full p-2 border rounded"
          >
            {[1, 2, 3, 4, 5].map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">Spirit of Cricket — {teamTwo || "Team Two"}</label>
          <select
            value={spiritTwo}
            onChange={(e) => setSpiritTwo(e.target.value)}
            className="w-full p-2 border rounded"
          >
            {[1, 2, 3, 4, 5].map((val) => (
              <option key={val} value={val}>{val}</option>
            ))}
          </select>
        </div>

        <textarea
          placeholder="General Comments"
          value={comments}
          onChange={(e) => setComments(e.target.value)}
          className="w-full p-2 border rounded"
        ></textarea>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>
      <p className="mt-2 text-sm text-gray-700 dark:text-gray-200">{status}</p>
    </div>
  );
}
